INSERT INTO `posts` (`id`, `title`, `meta_title`, `alias`, `status`, `type`, `views`, `feature`, `description`, `meta_description`, `content`, `id_use`, `thumb`, `published`, `created`, `updates`, `deleted`) VALUES
(6, 'Oppo F5 bất ngờ xuất hiện trên tay các thí sinh Hoa hậu Hoàn vũ 2017', 'Oppo F5 bất ngờ xuất hiện trên tay các thí sinh Hoa hậu Hoàn vũ 2017', 'oppo-f5-bat-ngo-xuat-hien-tren-tay-cac-thi-sinh-hoa-hau-hoan-vu-2017', 1, 'news', 0, 0, 'OPPO đã hé lộ cho người dùng biết rằng cuối tháng này họ sẽ chính thức tung ra một chiếc smartphone tầm trung đột phá với thiết kế hiện đại, khác hẳn so với những sản phẩm của họ trong suốt 3 năm qua. OPPO F5 sẽ có thiết kế màn hình tràn viền vô cực với tỷ lệ 18:9 độc đáo.', 'OPPO đã hé lộ cho người dùng biết rằng cuối tháng này họ sẽ chính thức tung ra một chiếc smartphone tầm trung đột phá với thiết kế hiện đại, khác hẳn so với những sản phẩm của họ trong suốt 3 năm qua. OPPO F5 sẽ có thiết kế màn hình tràn viền vô cực với tỷ lệ 18:9 độc đáo.', '<p><img alt=\"1.jpg\" src=\"http://www.hdvietnam.com/attachments/1-jpg.271536/\" />​</p>\r\n\r\n<p><br />\r\nBên cạnh màn hình vô cực, OPPO F5 cũng được trang bị RAM 4GB, các phím bấm phía trong màn hình, cảm biến vân tay ở mặt lưng phía sau. Camera trước chuyên chụp selfie với độ phân giải cực cao. Không giống như các dòng smartphone khác đang trang bị camera kép, F5 có vẻ chú trọng vào khả năng chụp hình và xử lý hình ảnh bá đạo được ưa chuộng trong suốt thời gian qua.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hôm qua, OPPO F5 đã xuất hiện lần đầu tiên trong một clip ca nhạc của Noo Phước Thịnh. Điều đó dấy lên dự đoán rằng ca sỹ điển trai đông fan này sẽ là đại diện sản phẩm chính thức tiếp theo của OPPO sau Tóc Tiên với F3+ và Sơn Tùng MTP với F3.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>​</p>\r\n\r\n<p><br />\r\nSau Noo Phước Thịnh, OPPO F5 lại tiếp tục lộ diện trên tay các thí sinh thi Hoa hậu Hoàn vũ tại Việt Nam năm nay. Hai màu xuất hiện là Đỏ và Xám, nhiều khả năng cũng sẽ là những màu được bán chính thức tại Việt Nam.<br />\r\n<br />\r\nTheo OPPO Việt Nam,&nbsp;<strong>F5 chính thức ra mắt tại thị trường VN vào ngày 4/11,&nbsp;</strong>sau đó sẽ cho đặt hàng trước ngay lập tức. Giá bán của OPPO F5 vẫn chưa được tiết lộ, nhưng dự đoán sẽ ở mức 6 đến 7 triệu.&nbsp;<br />\r\n<br />\r\n<strong>Một số hình ảnh:</strong>&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/f902d402bb962bae678235b9189b7c6e.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/83b3c04684502ca402c78748fa0fcbb7.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/f87fdc113cba49c67604663bd8f57ddb.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/1554f37c0fc6fc2da2274fde2429a6ce.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/b43e169b9298abdaa28c5a7651b62f60.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/b318003d1aa41fdc9f07cd08366643ad.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/75eef5b75a510d33d302873a1ced8ca7.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/a5cbe431f6813257693bac90ac65b469.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/13/3d9fc5f08799df5f9d5dc5d4d7dae7f4.jpg\" />​</p>\r\n', 8, 'http://i2.cdn8.net/-OHfc9thwto0/WZuQznVpSlI/AAAAAAAB1Gc/8BBCUQQtRz0B4ewqcQBdbTu-6V4wOe2gQCHMYCw/s0/cdn8.net1503367374.png', 1506704400, 1506742039, 1508682753, NULL),
(7, 'Đông đảo Nokia Fan đến tham dự offline giới thiệu Nokia 8', 'Đông đảo Nokia Fan đến tham dự offline giới thiệu Nokia 8', 'dong-dao-nokia-fan-den-tham-du-offline-gioi-thieu-nokia-8', 1, 'news', 1, 0, 'Ngày 14/10, một chuỗi sự kiện Offline đã được tổ chức để giới thiệu chiếc smartphone đỉnh nhất của Nokia hiện nay là Nokia 8. Đây cũng là dịp những người yêu thích Nokia được gặp gỡ và trải nghiệm thực tế trên tay chiếc điện thoại đang rất được trông đợi này.', 'Ngày 14/10, một chuỗi sự kiện Offline đã được tổ chức để giới thiệu chiếc smartphone đỉnh nhất của Nokia hiện nay là Nokia 8. Đây cũng là dịp những người yêu thích Nokia được gặp gỡ và trải nghiệm thực tế trên tay chiếc điện thoại đang rất được trông đợi này.', '<p><img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/15/e99f17fbf2d4a848425a8ef450023df4.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/15/11224364facfb07bdf3d0f3321b64f02.jpg\" /><br />\r\nĐại diện của HMD Global​</p>\r\n\r\n<p><br />\r\nSau khi đạt được thành công nhất định với 3 dòng Nokia 3, Nokia 5 và Nokia 6. Thương hiệu Nokia đã vươn lên top 4 tại thị trường Việt Nam, đánh bật những đối thủ sừng sỏ khác xuống dưới, điều đó cho thấy những người yêu mến thương hiệu đến từ Phần Lan này đang còn rất đông đảo và có tình yêu thương chưa đổi thay.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Buổi Offline đã thành công vượt ngoài mong đợi khi lượng khách mời tham dự đến cực kỳ đông, ngồi chật kín hết cả khán phòng. Ban Tổ Chức phải điều động thêm ghế, tuy vậy vẫn không đám ứng đủ nhu cầu, rất nhiều người đến sau phải đứng nhưng vẫn kiên định nghe giới thiệu và trải nghiệm Nokia 8 và những dòng smartphone khác của Nokia.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/15/49f35da8ca9218e0238703e38d320c75.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/15/4d049c826d2036848be34b59464a3079.jpg\" /><br />\r\nAnh Tài Hoàng, đại diện diễn đàn công nghệ lớn​</p>\r\n\r\n<p><br />\r\nĐại diện của Nokia và anh Tài Hoàng (đến từ diễn đàn công nghệ đông thành viên) đã giới thiệu những điểm mạnh nhất, nổi bật nhất của Nokia 8 và nhất là giới thiệu những tính năng mới thú vị của camera kép được trang bị lần này, như là tính năng quay phim chụp hình cả hai camera trước và sau cùng một lúc.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/15/d46121a7ef0d390743b3cbe791c281b9.jpg\" />​</p>\r\n\r\n<p><br />\r\nNgoài ra, tất cả khách hàng khi đến tham dự trải nghiệm các sản phẩm của Nokia đã nhận được những phần quà và voucher mua hàng hấp dẫn từ Nokia và các nhà bán lẻ. Đặc biệt, khi tham gia các trò chơi trong sự kiện này, người hâm mộ Nokia còn nhận được 40 balo sành điệu và tham gia bốc thăm trúng thưởng với giải nhất là một chiếc Nokia 8 và 3 giải nhì mỗi giải nhận được một chiếc Nokia 3310.</p>\r\n', 8, 'http://i2.cdn8.net/-OHfc9thwto0/WZuQznVpSlI/AAAAAAAB1Gc/8BBCUQQtRz0B4ewqcQBdbTu-6V4wOe2gQCHMYCw/s0/cdn8.net1503367374.png', 1506704400, 1506746865, 1508682595, NULL),
(8, 'Một vòng quanh Plase Show Hà Nội 2017 - Mạnh mẽ, sôi động và đa dạng', 'Một vòng quanh Plase Show Hà Nội 2017 - Mạnh mẽ, sôi động và đa dạng', 'mot-vong-quanh-plase-show-ha-noi-2017-manh-me-soi-dong-va-da-dang', 1, 'news', 0, 0, 'Năm năm qua, thị trường pro-audio phát triển vô cùng sôi động tại Việt Nam. Đây là 1 thị trường không mới với người tiêu dùng Việt, nhưng công nghệ, kỹ thuật của các sản phẩm đòi hỏi trình độ hiểu biết chuyên sâu và đào tạo bài bản, sản phẩm phức tạp từ khâu lắp đặt đến khâu vận hành, phân khúc sản phẩm hẹp đa phần chỉ dành cho giới chuyên nghiệp trong lĩnh vực tổ chức sự kiện.', 'Năm năm qua, thị trường pro-audio phát triển vô cùng sôi động tại Việt Nam. Đây là 1 thị trường không mới với người tiêu dùng Việt, nhưng công nghệ, kỹ thuật của các sản phẩm đòi hỏi trình độ hiểu biết chuyên sâu và đào tạo bài bản, sản phẩm phức tạp từ khâu lắp đặt đến khâu vận hành, phân khúc sản phẩm hẹp đa phần chỉ dành cho giới chuyên nghiệp trong lĩnh vực tổ chức sự kiện.', '<p>Do đó, PLASE SHOW là cơ hội để giới chuyên gia giới thiệu sản phẩm, những giải pháp, những công nghệ ra thị trường. Cùng với đó là những nhà tổ chức sự kiện, những đơn vị chuyên lắp đặt âm thanh ánh sáng sân khấu có cơ hội được trải nghiệm nghe thử thực tế, cũng như giao lưu, tìm kiếm đối tác thích hợp nhất.<br />\r\n<br />\r\nĐây mới là lần thứ 2 tổ chức, nhưng PLASE SHOW đã chứng minh được &ldquo;xứ mệnh&rdquo; của mình đối với thị trường thiết bị chuyên nghiệp. Quy mô lần thứ 2 tăng gấp đôi so với lần 1 tại thành phố Hồ Chí Minh hồi đầu tháng 5 vừa qua. Với 118 gian hàng trong nhà và 30 dàn loa ngoài trời, Plase show có khoảng gần 100 thương hiệu được giới thiệu và trình chiếu, trong đó phải kể đến những tên tuổi tiêu biểu như: L-Acoustics, RCF, Adamson, JBL, SE Technik, Db Technology, Wharfedale Pro&hellip;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/41568efdee56f6fbfa49b4971e6f093f.jpg\" />​</p>\r\n\r\n<p><br />\r\nSáng nay, Triển lãm đã chính thức khai mạc và sẽ diễn ra đến&nbsp;<strong>hết ngày 22/10 tại Cung Kiến Trúc và Quy Hoạch Quốc Gia</strong>. Khách tham quan có thể đến<strong>&nbsp;tham dự miễn phí</strong>, chỉ cần đăng ký thông tin ngay tại quầy của BTC.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chắc chắn, dàn Line Array ngoài trời sẽ là điểm nhấn đáng quan tâm nhất của bất cứ ai đến tham quan triển lãm. Với 28 hệ thống loa được trình diễn lần lượt theo khung thời gian cố định, người nghe sẽ có được những nhận xét đánh giá về sự khác nhau của các bộ dàn và tìm thấy cho mình một dàn loa sân khấu ưa thích nhất. Các đơn vị tổ chức sự kiện cũng có thể nghe và so sánh chất âm, uy lực, mạnh mẽ của từng bộ dàn.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/7640760324b765a6eb66f0a8ea5b60c3.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/cc265c8608bc9142c58dca6b0941b0f1.jpg\" />​</p>\r\n\r\n<p><br />\r\nSáng nay, rất nhiều người đã tập trung nghe lần lượt các bộ dàn ngoài trời trình diễn, mỗi bộ có một chất âm riêng, cũng như trình diễn thể loại nhạc khác nhau. Nhưng hầu hết đều cho người nghe thấy âm thanh sân khấu thực sự nó hay như thế nào, và có công suất lớn ra sao để phục vụ cho hàng ngàn khán giả cùng một lúc.<br />\r\n<br />\r\nBên cạnh đó, hệ thống ánh sáng chuyên nghiệp cũng là một điều đáng chú ý trong triển lãm, những màn trình diễn ánh sáng ấn tượng, mê hoặc người xem diễn ra liên tục tại các gian hàng chuyên về thiết bị ánh sáng.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/b616ac828303127c797d52f18cb94954.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/1caeb22432db89f4779c05d846ff6cfa.jpg\" />​</p>\r\n\r\n<p><br />\r\nCác hệ thống loa dành cho Karaoke cũng được trình diễn rất nhiều trong triển lãm lần này, Chủ yếu tập trung ở các dòng loa chuyên nghiệp, âm thanh cao cấp, lắp đặt trong những quán karaoke lớn hiện nay, để đáp ứng được nhu cầu của những khách hàng muốn đi hát ở những phòng karaoke đắt tiền, âm thanh hay. Ngoài ra, booth karaoke cá nhân cũng là một sản phẩm thu hút được nhiều sự quan tâm của mọi người. Khách tham quan có thể hát thử bằng micro và tai nghe, hát thoải mái những không gây ra tiếng ồn xung quanh, phù hợp với nhu cầu cá nhân, hát một mình hoặc luyện giọng.&nbsp;<br />\r\n<br />\r\n<strong>Một số hình ảnh tại triển lãm:&nbsp;</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/7f77693ba7b94e4694a87165169a0485.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/3cba8546568e8cd5b3fce80e10118526.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/0693192b023c8072e2a6747e49b2615f.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/66626e4108e6e304a2fa15037beaad6d.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/8706574407b6602334aeff8568249c11.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/f60e86ece567eea6bfe82901435fa4d9.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/66350be8879790dba27e6262e6c704ce.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/d34089ed2e81a1cbf2bee9eba6d4e7fa.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/c4f7fdcda1bcd4544676dff1ee8865b6.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/3ee84d695691614aff3ba830272fffc9.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/c7e6ab6ec7a1e8c0c0fd1d2d0ab3a1d6.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/c00ddfe96278b4d3ffd51af38f01d15c.jpg\" /><br />\r\n<img alt=\"[​IMG]\" src=\"https://imgit.org/i/2017/10/20/64003f44784b857ba2d1435c2731f755.